import { computed, onMounted, ref } from "vue";
export default {
  name: "TkDialog",
  props: {
    closeDialog: {
      type: Function,
      required: true
    },
    header: {
      type: String,
      required: true
    },
    event1: {
      default: false
    },
    event2: {
      default: false
    },
    event3: {
      default: false
    }
  },
  setup() {
    const dialogRef = ref();
    const maximized = computed(() => dialogRef.value && dialogRef.value.maximized);
    onMounted(() => {
      dialogRef.value.maximize();
    });
    return {
      dialogRef,
      maximized
    };
  }
};
